mapper.select:
select

from
CLASS_STAGE_SUBJECT_TEACHER a

    and a.CLASS_ID = #{classId}

        and a.START_DATE >=#{startDate}


        and #{endDate} >a.END_DATE

    and a.TEACHER_TYPE = 1

order by a.start_date desc

    limit
    ${(pageDto.pageNum-1)*pageDto.pageSize},#{pageDto.pageSize}

select count(a.ID)
from CLASS_STAGE_SUBJECT_TEACHER a

    and a.CLASS_ID = #{classId}

        and a.START_DATE >=#{startDate}


        and #{endDate} >a.END_DATE

    and a.TEACHER_TYPE = 1

mapper.sql:
SELECT NULL AS ID, NULL AS CLASS_ID, ltd.TEACHER_DEVICE_ID AS ROOM_ID, ltd.STAFF_NO AS TEACHER_STAFF_NO, 1 AS TEACHER_TYPE, lt.STAGE_ID AS STAGE_ID, lt.SUBJECT_ID AS SUBJECT_ID, 0 AS BATCH_NUM, lt.NODE_ID AS NODE_ID, 0 AS ACCIDENT_STATUS, '' AS ACCIDENT_DESC, '' AS SCORE_DETAIL, 0 AS SCORE, 0 AS SURVEY_NUM, MIN( lttd.START_TIME ) AS START_DATE, MAX( lttd.END_TIME ) AS END_DATE, SUN( lttd.DURATION ) AS DURATION, MIN( lttd.CREATE_TIME ) AS CREATE_TIME, MIN( lttd.UPDATE_TIME ) AS UPDATE_TIME, MIN( lttd.CREATOR_ID ) AS CREATOR_ID, NULL AS `STATUS`, 4 AS TEACH_WAY, ltd.TIME_TABLE_ID AS LESSON_TABLE_ID, NULL AS MASTER_CSST_ID, ltd.IS_LOCAL AS IS_LOCAL FROM lesson_table lt, lesson_table_detail ltd, lesson_table_time_detail lttd WHERE lt.ID = ltd.TIME_TABLE_ID AND ltd.ID = lttd.LESSON_DETAIL_ID AND lt.IS_DEL = 0 AND ltd.IS_DEL = 0 AND lttd.is_DEL = 0
AND ltd.END_TIME>#{startDate}
AND #{endDate} >ltd.START_TIME
AND ltd.STAFF_NO=#{teacherNo}
GROUP BY ltd.TIME_TABLE_ID,lt.STAGE_ID,lt.SUBJECT_ID,ltd.STAFF_NO

select a.ID from CLASS_STAGE_SUBJECT_TEACHER a join (select distinct c.csst_id from lesson_teacher b join class_lesson c on b.lesson_id = c.id where b.teacher_staff_no = #{teacherNo}) d on a.id = d.csst_id and a.TEACH_WAY=0
and DATE(a.END_DATE) >=#{startDate}
and #{endDate} >=DATE(a.START_DATE)

SELECT NULL AS ID FROM lesson_table lt, lesson_table_detail ltd, lesson_table_time_detail lttd WHERE lt.ID = ltd.TIME_TABLE_ID AND ltd.ID = lttd.LESSON_DETAIL_ID AND lt.IS_DEL = 0 AND ltd.IS_DEL = 0 AND lttd.is_DEL = 0
AND ltd.END_TIME>#{startDate}
AND #{endDate} >ltd.START_TIME
AND ltd.STAFF_NO=#{teacherNo}
GROUP BY ltd.TIME_TABLE_ID,lt.STAGE_ID,lt.SUBJECT_ID,ltd.STAFF_NO

mapper.select:
select
from CLASS_STAGE_SUBJECT_TEACHER a join (select distinct c.csst_id from lesson_teacher b join class_lesson c on b.lesson_id = c.id where b.teacher_staff_no = #{teacherNo}) d on a.id = d.csst_id
and DATE(a.END_DATE) >=#{startDate}
and #{endDate} >=DATE(a.START_DATE)
order by a.start_date desc
limit ${(pageDto.pageNum-1)*pageDto.pageSize},#{pageDto.pageSize}

select count(a.id) from CLASS_STAGE_SUBJECT_TEACHER a join (select distinct c.csst_id from lesson_teacher b join class_lesson c on b.lesson_id = c.id where b.teacher_staff_no = #{teacherNo}) d on a.id = d.csst_id
and DATE(a.END_DATE) >=#{startDate}
and #{endDate} >=DATE(a.START_DATE)

select distinct

from CLASS_STAGE_SUBJECT_TEACHER a
join CLASS_LESSON b on a.ID =
b.CSST_ID join LESSON_STUDENT c on
b.ID =
c.LESSON_ID

    c.STUDENT_ID =#{studentId}

        and a.TEACH_WAY = #{teachWay}


        and b.LESSON_DATE >= #{startDate}


        and #{endDate} > b.LESSON_DATE


order by a.start_date desc

    limit
    ${(pageDto.pageNum-1)*pageDto.pageSize},#{pageDto.pageSize}

select count(distinct a.ID) from CLASS_STAGE_SUBJECT_TEACHER a join CLASS_LESSON b on a.ID = b.CSST_ID join LESSON_STUDENT c on b.ID = c.LESSON_ID
c.STUDENT_ID =#{studentId}
and a.TEACH_WAY = #{teachWay}
and b.LESSON_DATE >= #{startDate}
and #{endDate} > b.LESSON_DATE

select IFNULL(sum(b.LESSON_DURATION),0)
from
CLASS_STAGE_SUBJECT_TEACHER a
join CLASS_LESSON
b on a.ID = b.CSST_ID

    and a.CLASS_ID =#{classId}
    and a.STAGE_ID =#{stageId}
    and
    a.SUBJECT_ID =#{subjectId}
    and b.IS_ADDITIONAL = 0

mapper.update:
update CLASS_STAGE_SUBJECT_TEACHER


        STATUS = #{status}


where ID in

    #{csstId}

update CLASS_STAGE_SUBJECT_TEACHER set score
=#{score}, survey_num
=#{surveyNum} , ACCIDENT_STATUS =#{accident}

    ,score_detail =#{scoreDetail}

where class_id =#{classId} and stage_id
=#{stageId} and subject_id
=#{subjectId} and TEACHER_STAFF_NO =
#{teacherNo} and TEACHER_TYPE=#{teacherType} and BATCH_NUM=#{batchNum}

mapper.select:
SELECT A.TEACHER_STAFF_NO , A.TEACHER_TYPE , MIN(B.START_TIME) START_DATE , MAX(B.END_TIME) END_DATE , SUM(B.LESSON_DURATION) DURATION , C.ID , A.CLASS_ID , A.STAGE_ID , A.SUBJECT_ID , A.BATCH_NUM , A.NODE_ID , B.TEACH_WAY , A.IS_LOCAL , C.ACCIDENT_STATUS FROM LESSON_TEACHER A JOIN CLASS_LESSON B ON A.LESSON_ID = B.ID LEFT JOIN CLASS_STAGE_SUBJECT_TEACHER C ON C.CLASS_ID = A.CLASS_ID AND C.STAGE_ID = A.STAGE_ID AND C.SUBJECT_ID = A.SUBJECT_ID AND C.BATCH_NUM = A.BATCH_NUM AND A.TEACHER_STAFF_NO = C.TEACHER_STAFF_NO AND A.TEACHER_TYPE = C.TEACHER_TYPE
A.CLASS_ID = #{classId} AND A.STAGE_ID = #{stageId} AND A.BATCH_NUM = #{batchNum} AND A.SUBJECT_ID = #{subjectId}
GROUP BY A.TEACHER_STAFF_NO , A.TEACHER_TYPE

SELECT (
		SELECT id
		FROM class_stage_subject_teacher
		WHERE class_id = #{classId}
			AND stage_id = #{stageId}
			AND subject_id = #{subjectId}
			AND batch_num = #{batchNum}
			AND TEACHER_STAFF_NO = ''
		LIMIT 1
	) AS id, a.class_id, a.stage_id, a.subject_id,a.batch_num, a.node_id
	, MIN(a.START_TIME) AS START_DATE, MAX(a.END_TIME) AS END_DATE
	, SUM(a.LESSON_DURATION) AS DURATION
FROM accident_class_lesson a
WHERE a.class_id = #{classId}
	AND a.stage_id = #{stageId}
	AND a.subject_id = #{subjectId}
	AND a.batch_num = #{batchNum}
	AND a.TEACHER_STAFF_NO = ''

SELECT (
		SELECT id
		FROM class_stage_subject_teacher
		WHERE class_id = #{classId}
			AND stage_id = #{stageId}
			AND subject_id = #{subjectId}
			AND batch_num = #{batchNum}
			AND TEACHER_STAFF_NO = ''
		LIMIT 1
	) AS id, a.class_id, a.stage_id, a.subject_id,a.batch_num, a.node_id
	, MIN(a.START_TIME) AS START_DATE, MAX(a.END_TIME) AS END_DATE
	, SUM(a.LESSON_DURATION) AS DURATION
	, a.TEACH_WAY
	, a.IS_LOCAL
FROM class_lesson a
WHERE a.class_id = #{classId}
	AND a.stage_id = #{stageId}
	AND a.subject_id = #{subjectId}
	AND a.batch_num = #{batchNum}
	AND a.TEACHER_STAFF_NO = ''

mapper.delete:
DELETE FROM class_stage_subject_teacher
WHERE class_id = #{classId}
	AND stage_id = #{stageId}
	AND subject_id = #{subjectId}
	AND batch_num = #{batchNum}
	AND teacher_type = 2
	AND NOT EXISTS (
		SELECT 1
		FROM lesson_teacher b
		WHERE b.csst_id = class_stage_subject_teacher.id
	)

DELETE FROM class_stage_subject_teacher
WHERE class_id = #{classId}
	AND stage_id = #{stageId}
	AND subject_id = #{subjectId}
	AND batch_num = #{batchNum}
	AND teacher_type = 1
	AND ACCIDENT_STATUS= 0
	AND NOT EXISTS (
		SELECT 1
		FROM class_lesson b
		WHERE b.csst_id = class_stage_subject_teacher.id
	)

