mapper.select:
select order_no,stage_id,subject_id from order_class_stage_subject

	and class_id=#{classId}

		and order_no in

			#{orderNo}

select 
	order_no 
from 
	order_class_stage_subject 


		and class_id=#{classId}


		and stage_id = #{stageId}


		and subject_id=#{subjectId} 


		and batch_num=#{batchNum}

SELECT

FROM order_class_stage_subject


		AND ORDER_NO=#{orderNo,jdbcType=VARCHAR}


		AND CLASS_ID=#{classId,jdbcType=BIGINT}


		AND CLASS_NO=#{classNo,jdbcType=VARCHAR}


		AND GOODS_NO=#{goodsNo,jdbcType=VARCHAR}


		AND STAGE_ID=#{stageId,jdbcType=BIGINT}


		AND SUBJECT_ID=#{subjectId,jdbcType=BIGINT}


		AND CREATE_TIME=#{createTime,jdbcType=TIMESTAMP}


		AND UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}


		AND CREATOR_ID=#{creatorId,jdbcType=BIGINT}


		AND BATCH_NUM=#{batchNum,jdbcType=INTEGER}

SELECT DISTINCT GOODS_NO FROM order_class_stage_subject WHERE CLASS_NO = #{classNo}
AND (STAGE_ID, BATCH_NUM) IN
(#{item.stageId}, #{item.batchNum})

SELECT oi.order_no as order_no, ocss.CLASS_NO as CLASS_NO, oi.STUDENT_ID as STUDENT_ID, oi.STUDENT_NAME as STUDENT_NAME, ocss.STAGE_ID as STAGE_ID, ocss.SUBJECT_ID as SUBJECT_ID, ocss.BATCH_NUM as BATCH_NUM FROM order_class_stage_subject ocss LEFT JOIN order_info oi ON ocss.ORDER_NO = oi.ORDER_NO WHERE 1 =1
ocss.CLASS_ID = #{classId}
AND ocss.ORDER_NO IN
#{item}

SELECT DISTINCT ocss.order_no FROM order_class_stage_subject ocss WHERE ocss.CLASS_ID = #{classId}
AND ocss.ORDER_NO IN ( SELECT oi.ORDER_NO FROM ORDER_INFO oi WHERE oi.STUDENT_ID like pattern1 OR oi.STUDENT_NAME like pattern1 )
ORDER BY CREATE_TIME DESC
limit ${(pageDto.pageNum-1)*pageDto.pageSize},#{pageDto.pageSize}
