mapper.select:
SELECT tem.* FROM (SELECT DISTINCT oss.* FROM order_stage_subject oss, order_class_stage_subject ocss WHERE oss.order_no = ocss.order_no AND ocss.class_id = #{classId}
AND OCSS.ORDER_NO = #{orderNo}
) AS tem WHERE NOT EXISTS (SELECT 1 FROM order_class_stage_subject ocss WHERE tem.order_no = ocss.order_no AND tem.stage_id = ocss.stage_id AND tem.subject_id = ocss.subject_id)

mapper.update:
update order_stage_subject set status = #{status}
where order_no in

    #{orderNo}

and stage_id = #{stageId} and subject_id = #{subjectId} and #{status}>status

mapper.select:
SELECT

FROM order_stage_subject


    AND GOODS_NO=#{goodsNo,jdbcType=VARCHAR}


    AND ORDER_NO=#{orderNo,jdbcType=VARCHAR}


    AND OLD_ORDER_NO=#{oldOrderNo,jdbcType=VARCHAR}


    AND STAGE_ID=#{stageId,jdbcType=BIGINT}


    AND SUBJECT_ID=#{subjectId,jdbcType=BIGINT}


    AND ENABLE=#{enable,jdbcType=INTEGER}


    AND TOTAL_DURATION=#{totalDuration,jdbcType=BIGINT}


    AND PAIKE_DURATION=#{paikeDuration,jdbcType=BIGINT}


    AND FINISH_DURATION=#{finishDuration,jdbcType=BIGINT}


    AND STATUS=#{status,jdbcType=INTEGER}


    AND CREATOR_ID=#{creatorId,jdbcType=BIGINT}


    AND START_DATE=#{startDate,jdbcType=TIMESTAMP}


    AND END_DATE=#{endDate,jdbcType=TIMESTAMP}


    AND CREATE_TIME=#{createTime,jdbcType=TIMESTAMP}


    AND UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}


    AND LESSON_TABLE_ID=#{lessonTableId, jdbcType=BIGINT}


    AND ORDER_NO IN

        #{item}

SELECT oss.* FROM order_stage_subject oss LEFT JOIN class_stage_subject css ON oss.stage_id = css.stage_id AND oss.subject_id = css.subject_id AND oss.total_duration = css.TOTAL_DURATION WHERE css.CLASS_ID = #{classId}
AND oss.order_no IN
#{item}

SELECT
    oss.ID
FROM
    order_stage_subject oss JOIN order_info oi ON oss.order_no=oi.order_no AND oi.ORDER_STATE!=7 AND oi.BUY_TIME BETWEEN #{startTime} AND #{endTime}
WHERE
    oss.START_DATE IS NOT NULL AND oss.START_DATE BETWEEN #{startTime} AND #{endTime}
